mapper.select:
select id, user_id, log_info, create_time, log_type
from t_admin_log
where create_time = #{date}

select id, user_id, log_info, create_time, log_type
from t_user_log
where create_time = #{date}

select id, user_id, log_info, create_time, log_type
from t_user_log
where user_id = #{id}

select id, user_id, log_info, create_time, log_type
from t_admin_log
where user_id = #{id}
